import React from 'react';
import './booking.css';
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from 'reactstrap'
const Booking = ({ tour, avgRating }) => {

  const { price, reviews } = tour;


  const handleChange = e => { };

  return (
    <div className="booking">
      <div className="booking__top d-flex align-items-center 
            justify-content-between">
        <h3>
          ${price} <span>/per person</span>
        </h3>
        <span className="tour__rating d-flex align-items-center ">
          <i class="ri-star-smile-fill"></i>
          {avgRating === 0 ? null : avgRating} ({reviews?.length})
        </span>
      </div>

      {/* ==============booking form========== */}


      {/* ==============booking end========== */}


      {/* =========booking bootom */}

        <div className="booking__bottom">
           <ListGroup>
            <ListGroupItem className="border-0 px-0">
           <h5 className="d-flex align-items-center gap-1">
            ${price}
             <i class="ri-close-fill"></i> 1 person
             </h5>
           <span>${price}</span>
            </ListGroupItem>
            <ListGroupItem className="border-0 px-0">
           <h5>Services charge</h5>
           <span>$10</span>
            </ListGroupItem><ListGroupItem className="border-0 px-0 total">
           <h5>Total</h5>
           <span>$299</span>
            </ListGroupItem>
           </ListGroup>
           <button className="btn primary__btn w-100 mt-4">BOOK NOW</button>
        </div>
    </div>
  );
};

export default Booking;
